@decor
def hello_1(some_list):
    for i in some_list:
        return f' Привет, {i.split()[-1].capitalize()}!'


# Для оптимизации - встроенная функция map. Без map -  0.01171875 Mib, с map - 0.0 Mib


@decor
def hello_2(some_list):
    return f' Привет, {str(map(lambda i: i.split()[-1].capitalize(), some_list))}'


print(hello_1(some_list))
print(hello_2(some_list))
